#Initialclass Solution:    def smallerNumbersThanCurrent(self, nums: list[int]) -> list[int]:        lst = []        for i in nums:            ind = 0            for j in nums:                if i > j:                    ind += 1            lst.append(ind)                    return lst#sorted_listclass Solution:    def smallerNumbersThanCurrent(self, nums: list[int]) -> list[int]:        lst = []        hash_map = {}        length = len(nums)        sorted_list = sorted(nums)                for i in range(length):            if sorted_list[i] not in hash_map:                hash_map[sorted_list[i]] = i                 for j in nums:            lst.append(hash_map[j])                    return lstprint(Solution().smallerNumbersThanCurrent(([6,5,4,8])))