#Initialclass Solution:    def longestCommonPrefix(self, strs: list[str]) -> str:        if len(strs) == 1:            return strs[0]                min_length = 200        for i in strs:            if len(i) == 0:                return ""            else:                if len(i) < min_length:                    min_length = len(i)        res = ""        for j in range(min_length):            for i in strs:                if strs[0][j] != i[j]:                    return res            res += strs[0][j]                return resclass Solution:    def longestCommonPrefix(self, strs: list[str]) -> str:        if len(strs) == 1:            return strs[0]                minimum, maximum = min(strs), max(strs)                for i in range(len(minimum)):            if minimum[i] != maximum[i]:                return minimum[:i]        return minimum    print(Solution().longestCommonPrefix(["aieiofs","aieiofs","aiaaaaaa"]))